import Form from 'react-bootstrap/Form';
import axios from 'axios';
import dotenv from 'dotenv'
// require('dotenv').config();

dotenv.config()
class App extends React.Component {

  constructor(props){
    super(props);
    this.state={
      allcountry:[]
    }
}

handleSubmit=async(e)=>{
  e.preventDefault(e);
  let q=e.target.value.trim();
  console.log(q);
  if(!q) return false;
