         <>
        <Form >
          <Form.Label>Email address</Form.Label>
          <Form.Control type="text" placeholder="Enter citiry" id="city"onChange={this.handleSubmit}/>
    </Form>
         </>

    <Footer />

    </>


    </div>
  );}
}

export default App;
